import React from "react";
import { useForm } from "react-hook-form";
import { nanoid } from "nanoid";

const TaskForm = ({ kisiler, submitFn }) => {
  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
  } = useForm();

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    const currentValue = Array.from(new Set([...getValues("people"), value]));
    const updatedValue = checked
      ? currentValue
      : currentValue.filter((p) => p !== value);
    setValue("people", updatedValue);
  };

  const handleOthersChange = (e) => {
    setValue(e.target.name, e.target.value);
  };

  const onSubmit = (data) => {
    submitFn({
      ...data,
      id: nanoid(5),
      status: "yapılacak",
    });
    setValue("title", "");
    setValue("description", "");
    setValue("people", []);
  };

  return (
    <form className="taskForm" onSubmit={handleSubmit(onSubmit)}>
      <div className="form-line">
        <label className="input-label" htmlFor="title">
          Başlık
        </label>
        <input
          className="input-text"
          id="title"
          name="title"
          type="text"
          {...register("title", { required: "Task başlığı yazmalısınız" })}
        />
        <p className="input-error">{errors.title?.message}</p>
      </div>

      <div className="form-line">
        <label className="input-label" htmlFor="description">
          Açıklama
        </label>
        <textarea
          className="input-textarea"
          rows="3"
          id="description"
          name="description"
          {...register("description", {
            required: "Task açıklaması yazmalısınız",
          })}
        ></textarea>
        <p className="input-error">{errors.description?.message}</p>
      </div>

      <div className="form-line">
        <label className="input-label">İnsanlar</label>
        <div>
          {kisiler.map((p) => (
            <label className="input-checkbox" key={p}>
              <input
                type="checkbox"
                name="people"
                value={p}
                onChange={handleCheckboxChange}
                checked={getValues("people")?.includes(p)}
              />
              {p}
            </label>
          ))}
        </div>
        <p className="input-error">{errors.people?.message}</p>
      </div>

      <div className="form-line">
        <button
          className="submit-button"
          type="submit"
          disabled={!formState.isValid}
        >
          Kaydet
        </button>
      </div>
    </form>
  );
};

export default TaskForm;
