import React from "react";
import { useForm } from "react-hook-form";
import { useState ,useEffect } from "react";
export default function TaskHookForm({ kisiler, submitFn }) {

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    people: [],
  });
  const [buttonDisabled, setButtonDisabled] = useState(true);

 // checkboxların değişimini state içerisine eklemek için özel fonksiyon
 function handleCheckboxChange(e) {
  const { value } = e.target;

  let yeniPeople = [...formData.people];
  const index = formData.people.indexOf(value);
  if (index > -1) {
    yeniPeople.splice(index, 1);
  } else {
    yeniPeople.push(value);
  }

  formAlaniniKontrolEt("people", yeniPeople);

}


// diğer form alanları değiştikçe çalışan ve yeni değeri state'e ekleyen fonksiyon
function handleOthersChange(e) {
  const { name, value } = e.target;
  formAlaniniKontrolEt(name, value);
  setFormData({
    ...formData,
    [name]: value,
  });
}

// task ekleme
function handleSubmit(e) {
  e.preventDefault();
  submitFn({
    ...formData,
    id: nanoid(5),
    status: "yapılacak",
  });
  setFormData({
    title: "",
    description: "",
    people: [],
  });
}

  const {
    register,
    control,
    submissionId,
    formState: { errors, isSubmitting },
  } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <form className="taskForm" onSubmit={handleSubmit(onSubmit)}>
      <div className="form-line">
        <label className="input-label" htmlFor="title">
          Başlık
        </label>
        <input
          className="input-text"
          {...register("title", {
            required: "Zorunlu alan boş bırakmayınız.",
            minLength: {
              value: 3,
              message: "En az 3 karakter girilmelidir.",
            },
          })}
          aria-invalid={errors["title"] ? "true" : "false"}
          placeholder="Başlık giriniz."
          type="text"
        />
        <p className="input-error">
          {errors["title"] && <p role="alert">{errors["title"]?.message}</p>}
        </p>
      </div>

      <div className="form-line">
        <label className="input-label" htmlFor="description">
          Açıklama
        </label>
        <textarea
          className="input-textarea"
          name="description"
          placeholder="Açıklama giriniz."
          {...register("description", {
            required: "Zorunlu alan boş bırakmayınız.",
            minLength: {
              value: 10,
              message: "En az 10 karakter girilmelidir.",
            },
          })}
        ></textarea>
        <p className="input-error">
          {errors["description"] && (
            <p role="alert">{errors["description"]?.message}</p>
          )}
        </p>
      </div>

      <div className="form-line">
        <label className="input-label">İnsanlar</label>
        <div>
          {kisiler.map((p) => (
          <label className="input-checkbox" key={p}>
            <input
              type="checkbox"
              name="people"
              value={p}
              onChange={handleCheckboxChange}

            />
            {p}
          </label>
        ))}
        </div>
      </div>

      <div className="form-line">
        <button className="submit-button" type="submit">
          Kaydet
        </button>
      </div>
    </form>
  );
}
